package com.code.hackerrank.algorithm;

import java.util.ArrayList;
import java.util.List;

public class P001_Common_child {
	public static void main(String[] args) {
		System.out.println(commonChild(s1, s2));
		System.out.println(list);
	}
	
	static List<String> list = new ArrayList<>();
	static Integer[][] memo;
	static int commonChild(String s1, String s2) {
		memo = new Integer[s1.length()+1][s2.length()+1];
		return LCS_bottomUp(s1, s2);
    }
	
	static int LCS_bottomUp(String s1, String s2) {
		for(int i=0; i<=s1.length(); i++) {
			for(int j=0; j<=s2.length(); j++) {
				if(i==0 || j==0) {
					memo[i][j] = 0;
				}else if(s1.charAt(i-1) == s2.charAt(j-1)) {
					list.add(String.valueOf(s1.charAt(i-1)));
					memo[i][j] = 1 + memo[i-1][j-1];
				}else {
					memo[i][j] = Math.max(memo[i-1][j], memo[i][j-1]);
				}
			}
		}
		return memo[s1.length()][s2.length()];
	}
	
	static int LCS_recursion(String s1, String s2, int n, int m) {
		if(memo[n][m] != null) {
			return memo[n][m];
		}
		int result;
		if(n==0 || m==0) {
			result = 0;
		}else if(s1.charAt(n-1) != s2.charAt(m-1)) {
			int r1 = LCS_recursion(s1,s2,n-1,m);
			int r2 = LCS_recursion(s1,s2,n,m-1);
			result = Math.max(r1, r2);
		}else {
			result = 1 + LCS_recursion(s1, s2, n-1, m-1);
		}
		
		memo[n][m] = result;
		return result;
	}

	static String s1 = "HARRY";
	static String s2 = "SAALLY";
	static String s11 = "";
	static String s22 = "";
}
